mapper.insert:
insert into t_class (id, grade, classno
  )
values (#{id,jdbcType=INTEGER}, #{grade,jdbcType=INTEGER}, #{classno,jdbcType=INTEGER}
  )

mapper.update:
update t_class
set grade = #{grade,jdbcType=INTEGER},
  classno = #{classno,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, grade, classno
from t_class
where id = #{id,jdbcType=INTEGER}

select max(classno)
from t_class
where grade = #{grade,jdbcType=INTEGER}
